import React from 'react';
import TodoPage from '../pages/todo';

class Todo extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            items: [],
        };

        this.onNewItem = this.onNewItem.bind(this);
    }


    onNewItem() {
        this.setState({
            items: [
                ...this.state.items,
                {
                    id: Date.now(),
                    text: '',
                    isChecked: false,
                    isEditting: true, 
                },
            ],
        })
    }

    render() {
        const {
            items
        } = this.state;

        return (
            <TodoPage
                items={items}
                onNewItem={this.onNewItem}
            />
        );
    }
}

export default Todo;
